","keccak256":"0x5c6caab697d302ad7eb59c234a4d2dbc965c1bae87709bd2850060b7695b28c7","license":"MIT"},"contracts/TokenFactory.sol":{"content":"//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "./interfaces/ITokenFactory.sol";
import "./VotesToken.sol";
import "@openzeppelin/contracts/utils/Create2.sol";
import "@fractal-framework/core-contracts/contracts/ModuleFactoryBase.sol";

/// @notice Token Factory used to deploy votes tokens
contract TokenFactory is ModuleFactoryBase, ITokenFactory {
    function initialize() external initializer {
        __initFactoryBase();
    }

    /// @dev Creates an ERC-20 votes token
    /// @param creator The address creating the module
    /// @param data The array of bytes used to create the token
    /// @return address The address of the created token
    function create(address creator, bytes[] calldata data)
        external
        override(ModuleFactoryBase, ITokenFactory)
        returns (address[] memory)
    {
        address[] memory createdContracts = new address[](1);

        string memory name = abi.decode(data[0], (string));
        string memory symbol = abi.decode(data[1], (string));
        address[] memory hodlers = abi.decode(data[2], (address[]));
        uint256[] memory allocations = abi.decode(data[3], (uint256[]));
        bytes32 salt = abi.decode(data[4], (bytes32));

        createdContracts[0] = _createToken(
            creator,
            salt,
            name,
            symbol,
            hodlers,
            allocations
        );

        return createdContracts;
    }

    function _createToken(
        address creator,
        bytes32 salt,
        string memory name,
        string memory symbol,
        address[] memory _hodlers,
        uint256[] memory _allocations
    ) internal returns (address createdToken) {
        createdToken = Create2.deploy(
            0,
            keccak256(
                abi.encodePacked(creator, msg.sender, block.chainid, salt)
            ),
            abi.encodePacked(
                type(VotesToken).creationCode,
                abi.encode(name, symbol, _hodlers, _allocations)
            )
        );
        emit TokenCreated(createdToken);
        return createdToken;
    }
}
","keccak256":"0x04a253ced81e55620e9d90e831cb3aa427360d263a81b346ed7ff6e568f2cbe4","license":"Unlicense"},"contracts/VotesToken.sol":{"content":"//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165Storage.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Votes.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Snapshot.sol";

contract VotesToken is IERC20, ERC20Snapshot, ERC20Votes, ERC165Storage {
    constructor(
        string memory _name,
        string memory _symbol,
        address[] memory _hodlers,
        uint256[] memory _allocations
    ) ERC20(_name, _symbol) ERC20Permit(_name) {
        _registerInterface(type(IERC20).interfaceId);
        for (uint256 i = 0; i < _hodlers.length; i++) {
            _mint(_hodlers[i], _allocations[i]);
        }

    }

    function captureSnapShot() external returns (uint256 snapId) {
        snapId = _snapshot();
    }

    // The functions below are overrides required by Solidity.
    function _mint(address to, uint256 amount)
        internal
        virtual
        override(ERC20, ERC20Votes)
    {
        super._mint(to, amount);
    }

    function _burn(address account, uint256 amount)
        internal
        virtual
        override(ERC20, ERC20Votes)
    {
        super._burn(account, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override(ERC20, ERC20Snapshot) {
        super._beforeTokenTransfer(from, to, amount);
    }

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override(ERC20, ERC20Votes) {
        super._afterTokenTransfer(from, to, amount);
    }
}
